package javaIO;

public class javaIO {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "홍길동";
		int age = 30;
		// 홍길동의 나이는 30 입니다.
		System.out.println(name + "의 나이는 " + age + "입니다.");
		System.out.printf("%s의 나이는 %d입니다.\n", name, age);
		// 소수점 %f, bool %b
		System.out.print("d");
	}

}
